            $params = array(
				'nombre_estado' => $this->input->post('nombre_estado'),
            );
            
            $estado_entrega_id = $this->Estado_entrega_model->add_estado_entrega($params);
            redirect('estado_entrega/index');
        }
        else
        {            
            $data['_view'] = 'estado_entrega/add';
            $this->load->view('layouts/main',$data);
        }
    }  

    /*
     * Editing a estado_entrega
     */
    function edit($id_estado_entrega)
    {   
        // check if the estado_entrega exists before trying to edit it
        $data['estado_entrega'] = $this->Estado_entrega_model->get_estado_entrega($id_estado_entrega);
        
        if(isset($data['estado_entrega']['id_estado_entrega']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'nombre_estado' => $this->input->post('nombre_estado'),
                );

                $this->Estado_entrega_model->update_estado_entrega($id_estado_entrega,$params);            
                redirect('estado_entrega/index');
            }
            else
            {
                $data['_view'] = 'estado_entrega/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
