    } 

    /*
     * Deleting estado_entrega
     */
    function remove($id_estado_entrega)
    {
        $estado_entrega = $this->Estado_entrega_model->get_estado_entrega($id_estado_entrega);

        // check if the estado_entrega exists before trying to delete it
        if(isset($estado_entrega['id_estado_entrega']))
        {
            $this->Estado_entrega_model->delete_estado_entrega($id_estado_entrega);
            redirect('estado_entrega/index');
