    /*
     * Deleting fk_categoria
     */
    function remove($id_fk_categoria)
    {
      $fk_categoria_lm = $this->Fk_categorias_lm_model->get_fk_categorias_lm($id_fk_categoria);

      // check if the sector_req exists before trying to delete it
      if(isset($fk_categoria_lm['id_cat_lm']))
      {
        if($fk_categoria_lm['status_cat_lm'] == $this->config->item('Inactivo')){
            $array = array(
                'status_cat_lm' => $this->config->item('Activo')
            );
        }else{
            $array = array(
                'status_cat_lm' => $this->config->item('Inactivo')
            );
        }
          $this->Fk_categorias_lm_model->update_fk_categorias_lm($fk_categoria_lm['id_cat_lm'],$array);
          redirect('fk_categorias_lm/index');
      }
      else
